class Solution:
    def maximumSubsequenceCount(self, text: str, pattern: str) -> int:
        cnt_p1, cnt_p2 = 0, 0
        total_num = 0
        if pattern[0] == pattern[1]:
            cnt = text.count(pattern[0])
            return (1 + cnt) * (cnt) // 2
        for i in text:
            if i == pattern[0]:
                cnt_p1 += 1
            elif i == pattern[1]:
                cnt_p2 += 1
                total_num += cnt_p1
        total_num += max(cnt_p1, cnt_p2)
        return total_num

